// Download AngularJS via CDN
<script src="https://ajax.googleapis.com/ajax/libs/angularjs/1.8.2/angular.min.js"></script>

// Example of setting up a basic AngularJS app
var app = angular.module('myApp', []);

app.controller('myCtrl', function($scope) {
  $scope.message = 'Hello, AngularJS!';
});

// Example of a controller in AngularJS
app.controller('myCtrl', function($scope) {
  $scope.message = 'Hello, AngularJS!';
});

// Example of a directive in AngularJS
app.directive('myDirective', function() {
  return {
    template: '<div>My Directive</div>',
    restrict: 'E'
  };
});

// Example of data binding in AngularJS
<input type="text" ng-model="name">
<p>Hello, {{ name }}!</p>

// Example of a service and dependency injection in AngularJS
app.service('myService', function() {
  this.sayHello = function() {
    return 'Hello, AngularJS Service!';
  };
});

app.controller('myCtrl', function($scope, myService) {
  $scope.message = myService.sayHello();
});

// Example of routing in AngularJS
app.config(function($routeProvider) {
  $routeProvider
    .when('/', {
      templateUrl: 'home.html',
      controller: 'homeController'
    })
    .when('/about', {
      templateUrl: 'about.html',
      controller: 'aboutController'
    })
    .otherwise({
      redirectTo: '/'
    });
});

// Example of using filters in AngularJS
<p>{{ message | uppercase }}</p>
<p>{{ amount | currency }}</p>

// Example of making an HTTP request in AngularJS
app.controller('myCtrl', function($scope, $http) {
  $http.get('/api/data').then(function(response) {
    $scope.data = response.data;
  });
});

// Example of unit testing a controller in AngularJS
describe('myCtrl', function() {
  beforeEach(module('myApp'));

  var $controller;
  beforeEach(inject(function(_$controller_){
    $controller = _$controller_;
  }));

  it('should initialize message correctly', function() {
    var $scope = {};
    var controller = $controller('myCtrl', { $scope: $scope });
    expect($scope.message).toEqual('Hello, AngularJS!');
  });
});

// Example of animation in AngularJS
app.animation('.my-animation', function() {
  return {
    enter: function(element, done) {
      element.css({ opacity: 0 });
      jQuery(element).animate({ opacity: 1 }, done);
    },
    leave: function(element, done) {
      element.css({ opacity: 1 });
      jQuery(element).animate({ opacity: 0 }, done);
    }
  };
});

// Example of an interceptor in AngularJS
app.factory('myInterceptor', function() {
  return {
    request: function(config) {
      // Modify config before sending
      return config;
    },
    response: function(response) {
      // Handle response
      return response;
    }
  };
});

app.config(function($httpProvider) {
  $httpProvider.interceptors.push('myInterceptor');
});

// Example of internationalization in AngularJS
app.config(function($translateProvider) {
  $translateProvider.translations('en', {
    'GREETING': 'Hello, World!'
  });

  $translateProvider.translations('es', {
    'GREETING': '¡Hola, Mundo!'
  });

  $translateProvider.preferredLanguage('en');
});

// Example usage in HTML: {{ 'GREETING' | translate }}

// Example of WebSocket integration in AngularJS
var socket = new WebSocket('ws://example.com/socket');

socket.onopen = function() {
  console.log('WebSocket connection opened.');
};

socket.onmessage = function(event) {
  console.log('Message from server: ', event.data);
};

socket.onclose = function() {
  console.log('WebSocket connection closed.');
};

// Example of using promises in AngularJS
app.controller('myCtrl', function($scope, $http) {
  $http.get('/api/data').then(function(response) {
    $scope.data = response.data;
  }, function(error) {
    $scope.error = 'Error fetching data.';
  });
});

// Example of authentication with JWT in AngularJS
app.controller('LoginCtrl', function($scope, $http, $window) {
  $scope.login = function() {
    $http.post('/api/login', $scope.user).then(function(response) {
      $window.localStorage.setItem('token', response.data.token);
    }, function(error) {
      $scope.errorMessage = 'Invalid credentials.';
    });
  };
});

// Example of a component in AngularJS
app.component('myComponent', {
  templateUrl: 'my-component.html',
  controller: function() {
    this.message = 'Hello, Component!';
  }
});

// Example of error handling in AngularJS
app.factory('errorInterceptor', function($q, $log) {
  return {
    responseError: function(rejection) {
      $log.error('HTTP Error: ', rejection);
      return $q.reject(rejection);
    }
  };
});

app.config(function($httpProvider) {
  $httpProvider.interceptors.push('errorInterceptor');
});

// Example of e2e testing with Protractor in AngularJS
describe('Protractor Demo App', function() {
  it('should add one and two', function() {
    browser.get('http://localhost:8000');

    element(by.model('first')).sendKeys(1);
    element(by.model('second')).sendKeys(2);

    element(by.id('gobutton')).click();

    expect(element(by.binding('latest')).getText()).toEqual('3');
  });
});

// Example of lazy loading in AngularJS
$stateProvider.state('home', {
  url: '/home',
  templateUrl: 'views/home.html',
  controller: 'HomeController',
  resolve: {
    load: function($ocLazyLoad) {
      return $ocLazyLoad.load('scripts/home.controller.js');
    }
  }
});

// Example of internationalization in AngularJS using Angular-translate
app.config(function($translateProvider) {
  $translateProvider.translations('en', {
    'GREETING': 'Hello, World!'
  });

  $translateProvider.translations('es', {
    'GREETING': '¡Hola, Mundo!'
  });

  $translateProvider.preferredLanguage('en');
});

// Example usage in HTML: {{ 'GREETING' | translate }}

