// Example of input validation in AngularJS
<input type="text" ng-model="username" required>
<div ng-show="myForm.username.$error.required">Username is required.</div>

// Example of OAuth authentication in AngularJS
app.controller('LoginCtrl', function($scope, $http, $window) {
  $scope.login = function() {
    $http.get('/api/auth/google').then(function(response) {
      $window.localStorage.setItem('token', response.data.token);
    }, function(error) {
      $scope.errorMessage = 'OAuth login failed.';
    });
  };
});
