#include <iostream>

void fibonacci(int n) {
    int first = 0, second = 1, next;
    std::cout << "Fibonacci Series up to " << n << " terms: ";
    for (int i = 1; i <= n; ++i) {
        std::cout << first << " ";
        next = first + second;
        first = second;
        second = next;
    }
}

int main() {
    int n;
    std::cout << "Enter the number of terms: ";
    std::cin >> n;
    fibonacci(n);
    return 0;
}

#include <iostream>

int gcd(int a, int b) {
    while (b != 0) {
        int temp = b;
        b = a % b;
        a = temp;
    }
    return a;
}

int main() {
    int num1, num2;
    std::cout << "Enter two numbers: ";
    std::cin >> num1 >> num2;
    std::cout << "GCD of " << num1 << " and " << num2 << " is " << gcd(num1, num2);
    return 0;
}

#include <iostream>
#include <string>

bool isPalindrome(std::string str) {
    int left = 0;
    int right = str.length() - 1;
    while (left < right) {
        if (str[left] != str[right])
            return false;
        ++left;
        --right;
    }
    return true;
}

int main() {
    std::string str;
    std::cout << "Enter a string: ";
    std::getline(std::cin, str);
    if (isPalindrome(str))
        std::cout << str << " is a palindrome.";
    else
        std::cout << str << " is not a palindrome.";
    return 0;
}

