#include <iostream>
#include <cmath>

bool isArmstrong(int num) {
    int sum = 0;
    int temp = num;
    int digits = (int) log10(num) + 1;
    while (temp != 0) {
        int remainder = temp % 10;
        sum += pow(remainder, digits);
        temp /= 10;
    }
    return sum == num;
}

int main() {
    int num;
    std::cout << "Enter a number: ";
    std::cin >> num;
    if (isArmstrong(num))
        std::cout << num << " is an Armstrong number.";
    else
        std::cout << num << " is not an Armstrong number.";
    return 0;
}

