solution:
% Calculate the sum of two numbers
a = 5;
b = 10;
sum = a + b;
disp(['Sum of ' num2str(a) ' and ' num2str(b) ' is: ' num2str(sum)])

% Plotting a sine wave
x = linspace(0, 2*pi, 100);
y = sin(x);
plot(x, y);
title('Sine Wave');
xlabel('Angle (radians)');
ylabel('sin(x)');
grid on;

% Finding roots of a quadratic equation
a = 1;
b = -3;
c = 2;

% Calculate roots
roots = roots([a b c]);

% Display roots
if isreal(roots)
    disp(['Roots are: ' num2str(roots)]);
else
    disp('Roots are complex');
end

% Generating a random matrix and finding its transpose
matrix = randi([1, 10], 3, 3);
transposeMatrix = matrix.';

% Display matrices
disp('Original Matrix:');
disp(matrix);
disp('Transpose Matrix:');
disp(transposeMatrix);

% Solving a system of linear equations
A = [2, 3; 4, 5];
B = [5; 7];

% Solve for X
X = A \ B;

% Display solution
disp(['Solution X: ' num2str(X.')]);

% Performing matrix multiplication
A = [1, 2; 3, 4];
B = [5, 6; 7, 8];

% Multiply matrices
C = A * B;

% Display result
disp('Result of matrix multiplication:');
disp(C);

% Finding the area of a circle
radius = 5;
area = pi * radius^2;
disp(['Area of circle with radius ' num2str(radius) ' is: ' num2str(area)]);

% Computing FFT of a signal
signal = [1, 2, 3, 4];
fftSignal = fft(signal);

% Display FFT
disp('FFT of signal:');
disp(fftSignal);

% Generating a 3D plot
[X, Y] = meshgrid(-2:0.1:2);
Z = X.^2 + Y.^2;

% Plotting
figure;
surf(X, Y, Z);
title('3D Plot of X^2 + Y^2');
xlabel('X');
ylabel('Y');
zlabel('Z');
grid on;

% Calculating the inverse of a matrix
A = [2, 3; 4, 5];

% Calculate inverse
inverseA = inv(A);

% Display inverse
if ~isnan(inverseA)
    disp('Inverse of matrix A:');
    disp(inverseA);
else
    disp('Inverse does not exist for matrix A');
end

% Generating a bar chart
x = [1, 2, 3, 4];
y = [10, 15, 7, 22];
bar(x, y);
title('Bar Chart');
xlabel('X-axis');
ylabel('Y-axis');
grid on;

% Performing grayscale conversion and edge detection
img = imread('image.jpg');
grayscaleImg = rgb2gray(img);
edgeImg = edge(grayscaleImg, 'canny');

% Display original and processed images
figure;
subplot(1, 2, 1);
imshow(img);
title('Original Image');
subplot(1, 2, 2);
imshow(edgeImg);
title('Edge Detected Image');

% Solving a system of nonlinear equations
fun = @(x) [x(1)^2 + x(2)^2 - 4; x(1) * x(2) - 1];
x0 = [1; 1];
options = optimset('Display', 'iter');
x = fsolve(fun, x0, options);

% Display solution
if ~isempty(x)
    disp(['Solution: x1 = ' num2str(x(1)) ', x2 = ' num2str(x(2))]);
else
    disp('No solution found');
end

% Performing basic statistics on a dataset
data = [10, 20, 15, 25, 30];
meanValue = mean(data);
medianValue = median(data);
stdDeviation = std(data);

% Display statistics
fprintf('Mean: %.2f\nMedian: %.2f\nStandard Deviation: %.2f\n', meanValue, medianValue, stdDeviation);

% Performing LU decomposition
A = [4, 3; 6, 3];

% Perform LU decomposition
[L, U] = lu(A);

% Display matrices
fprintf('Matrix A:\n');
disp(A);
fprintf('Lower Triangular Matrix L:\n');
disp(L);
fprintf('Upper Triangular Matrix U:\n');
disp(U);

% Implementing a simple neural network (perceptron)
inputs = [0, 1, 1; 1, 0, 1];
weights = [0.5; -0.5; 1];
bias = 0.2;

% Calculate output
output = heaviside(weights.' * inputs + bias);

% Display output
fprintf('Output of perceptron: %.2f, %.2f, %.2f\n', output);

% Performing interpolation on a dataset
x = [1, 2, 3, 4];
y = [10, 15, 7, 22];
xi = 1.5;
method = 'linear';

% Perform interpolation
yi = interp1(x, y, xi, method);

% Display interpolated value
fprintf('Interpolated value at x=%.1f using %s interpolation: %.2f\n', xi, method, yi);
