# Function definition

def is_prime(num):
    if num <= 1:
        return False
    for i in range(2, int(num ** 0.5) + 1):
        if num % i == 0:
            return False
    return True

# Example usage
print(is_prime(17))  # Output: True

# Function definition

def factorial(num):
    if num == 0 or num == 1:
        return 1
    result = 1
    for i in range(2, num + 1):
        result *= i
    return result

# Example usage
print(factorial(5))  # Output: 120

# Function definition

def find_largest(arr):
    if not arr:
        return None
    largest = arr[0]
    for num in arr:
        if num > largest:
            largest = num
    return largest

# Example usage
print(find_largest([3, 7, 2, 9, 1]))  # Output: 9

# Function definition

def count_vowels(s):
    vowels = 'aeiouAEIOU'
    count = 0
    for char in s:
        if char in vowels:
            count += 1
    return count

# Example usage
print(count_vowels('Hello World'))  # Output: 3

# Function definition

def sum_array(arr):
    return sum(arr)

# Example usage
print(sum_array([1, 2, 3, 4, 5]))  # Output: 15
