/* Using npm */
npm install tailwindcss

/* Using yarn */
yarn add tailwindcss

/* Using CDN */
<link href="https://cdn.jsdelivr.net/npm/tailwindcss@2.2.19/dist/tailwind.min.css" rel="stylesheet">

/* Customizing Tailwind CSS example */
module.exports = {
  theme: {
    extend: {
      colors: {
        'custom-blue': '#1c92d2',
        'custom-green': '#f2fcfe',
      },
    },
  },
  variants: {},
  plugins: [],
}

/* Tailwind CSS plugins example */
const plugin = require('tailwindcss/plugin')

module.exports = {
  theme: {
    extend: {},
  },
  variants: {},
  plugins: [
    plugin(function({ addUtilities }) {
      const newUtilities = {
        '.skew-10deg': {
          transform: 'skewY(-10deg)',
        },
        '.skew-15deg': {
          transform: 'skewY(-15deg)',
        },
      }
      addUtilities(newUtilities)
    })
  ],
}

/* Typography plugin example */
// Install the plugin
// npm install @tailwindcss/typography

// Add to tailwind.config.js
module.exports = {
  plugins: [
    require('@tailwindcss/typography'),
  ],
}

/* Apply the prose class to your content */
<article class="prose lg:prose-xl">
  <h1>Typography Example</h1>
  <p>This is an example of styled text using Tailwind CSS typography plugin.</p>
</article>

/* Form styles example */
// Install the plugin
// npm install @tailwindcss/forms

// Add to tailwind.config.js
module.exports = {
  plugins: [
    require('@tailwindcss/forms'),
  ],
}

/* Use the form styles in your HTML */
<form class="space-y-4">
  <input type="text" class="form-input mt-1 block w-full" placeholder="Enter your name">
  <select class="form-select mt-1 block w-full">
    <option>Option 1</option>
    <option>Option 2</option>
  </select>
  <button type="submit" class="bg-blue-500 text-white py-2 px-4 rounded">Submit</button>
</form>

/* Visibility utilities example */
<div class="visible p-4 bg-red-500">Visible</div>
<div class="invisible p-4 bg-green-500">Invisible</div>

/* Aspect ratio example */
// Install the plugin
// npm install @tailwindcss/aspect-ratio

// Add to tailwind.config.js
module.exports = {
  plugins: [
    require('@tailwindcss/aspect-ratio'),
  ],
}

/* Use the aspect-ratio class */
<div class="aspect-w-16 aspect-h-9">
  <iframe src="https://www.youtube.com/embed/dQw4w9WgXcQ" frameborder="0" allowfullscreen></iframe>
</div>

/* Container queries example */
// Install the plugin
// npm install @tailwindcss/container-queries

// Add to tailwind.config.js
module.exports = {
  plugins: [
    require('@tailwindcss/container-queries'),
  ],
}

/* Use container query classes */
<div class="container">
  <div class="cq-aspect-ratio">
    Container query example
  </div>
</div>
